function insertSort($array)
{
    $length = count($array);
    $tmp = ''; 

    for ($i=1; $i<$length; ++$i) {
        if ($array[$i] < $array[$i-1]) {
            $tmp = $array[$i];
            for ($j = $i-1; $j > -1 && $array[$j] > $tmp; --$j) {
                $array[$j+1] = $array[$j];
            }   
            $array[$j+1] = $tmp;
        }   
    }   
    return $array;
}

